import React, { useState, useEffect } from 'react';
import { productAPI } from '../services/api';

const FilterPanel = ({ filters, onFilterChange }) => {
  const [categories, setCategories] = useState([]);
  const [brands, setBrands] = useState([]);
  const [priceRange, setPriceRange] = useState({ minPrice: 0, maxPrice: 1000 });

  useEffect(() => {
    fetchFilterOptions();
  }, []);

  const fetchFilterOptions = async () => {
    try {
      const [categoriesRes, brandsRes, priceRes] = await Promise.all([
        productAPI.getCategories(),
        productAPI.getBrands(),
        productAPI.getPriceRange()
      ]);

      setCategories(categoriesRes.data);
      setBrands(brandsRes.data);
      setPriceRange(priceRes.data);
    } catch (error) {
      console.error('Error fetching filter options:', error);
    }
  };

  const handleCategoryChange = (category) => {
    onFilterChange({ ...filters, category: filters.category === category ? '' : category });
  };

  const handleBrandChange = (brand) => {
    onFilterChange({ ...filters, brand: filters.brand === brand ? '' : brand });
  };

  const handleRatingChange = (rating) => {
    onFilterChange({ ...filters, minRating: filters.minRating === rating ? '' : rating });
  };

  const handlePriceChange = (e) => {
    const { name, value } = e.target;
    onFilterChange({ ...filters, [name]: value });
  };

  const clearFilters = () => {
    onFilterChange({
      category: '',
      brand: '',
      minPrice: '',
      maxPrice: '',
      minRating: '',
      sort: 'newest'
    });
  };

  return (
    <div className="filter-panel">
      <div className="flex-between mb-20">
        <h2>Filters</h2>
        <button onClick={clearFilters} className="btn btn-secondary">Clear All</button>
      </div>

      <div className="filter-section">
        <h3>Price Range</h3>
        <div className="form-group">
          <input
            type="number"
            name="minPrice"
            placeholder="Min"
            className="form-control"
            value={filters.minPrice || ''}
            onChange={handlePriceChange}
          />
        </div>
        <div className="form-group">
          <input
            type="number"
            name="maxPrice"
            placeholder="Max"
            className="form-control"
            value={filters.maxPrice || ''}
            onChange={handlePriceChange}
          />
        </div>
      </div>

      <div className="filter-section">
        <h3>Rating</h3>
        <div className="filter-chips">
          {[4, 3, 2, 1].map((rating) => (
            <div
              key={rating}
              className={`chip ${filters.minRating === rating ? 'active' : ''}`}
              onClick={() => handleRatingChange(rating)}
            >
              {rating}‚≠ê & up
            </div>
          ))}
        </div>
      </div>

      {categories.length > 0 && (
        <div className="filter-section">
          <h3>Category</h3>
          <div className="filter-chips">
            {categories.map((category) => (
              <div
                key={category}
                className={`chip ${filters.category === category ? 'active' : ''}`}
                onClick={() => handleCategoryChange(category)}
              >
                {category}
              </div>
            ))}
          </div>
        </div>
      )}

      {brands.length > 0 && (
        <div className="filter-section">
          <h3>Brand</h3>
          <div className="filter-chips">
            {brands.slice(0, 10).map((brand) => (
              <div
                key={brand}
                className={`chip ${filters.brand === brand ? 'active' : ''}`}
                onClick={() => handleBrandChange(brand)}
              >
                {brand}
              </div>
            ))}
          </div>
        </div>
      )}

      <div className="filter-section">
        <h3>Sort By</h3>
        <select
          className="form-control"
          value={filters.sort || 'newest'}
          onChange={(e) => onFilterChange({ ...filters, sort: e.target.value })}
        >
          <option value="newest">Newest</option>
          <option value="price_asc">Price: Low to High</option>
          <option value="price_desc">Price: High to Low</option>
          <option value="rating">Top Rated</option>
        </select>
      </div>
    </div>
  );
};

export default FilterPanel;
