import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json'
  }
});

// Add token to requests
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

// Handle response errors
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// Auth APIs
export const authAPI = {
  register: (data) => api.post('/auth/register', data),
  login: (data) => api.post('/auth/login', data),
  logout: () => api.post('/auth/logout'),
  getCurrentUser: () => api.get('/auth/me'),
  refreshSession: () => api.post('/auth/refresh')
};

// Product APIs
export const productAPI = {
  getProducts: (params) => api.get('/products', { params }),
  getProductById: (id) => api.get(`/products/${id}`),
  getCategories: () => api.get('/products/categories'),
  getBrands: () => api.get('/products/brands'),
  getPriceRange: () => api.get('/products/price-range'),
  searchSuggestions: (query) => api.get('/products/suggestions', { params: { query } }),
  getRelatedProducts: (id) => api.get(`/products/${id}/related`),
  getPriceHistory: (id) => api.get(`/products/${id}/price-history`)
};

// Review APIs
export const reviewAPI = {
  addReview: (data) => api.post('/reviews', data),
  getProductReviews: (productId, params) => api.get(`/reviews/product/${productId}`, { params }),
  markHelpful: (reviewId) => api.post(`/reviews/${reviewId}/helpful`),
  getSentimentSummary: (productId) => api.get(`/reviews/product/${productId}/sentiment`),
  updateReview: (reviewId, data) => api.put(`/reviews/${reviewId}`, data),
  deleteReview: (reviewId) => api.delete(`/reviews/${reviewId}`)
};

// Order APIs
export const orderAPI = {
  createOrder: (data) => api.post('/orders', data),
  getUserOrders: (params) => api.get('/orders', { params }),
  getOrderById: (orderId) => api.get(`/orders/${orderId}`),
  updateOrderStatus: (orderId, data) => api.put(`/orders/${orderId}/status`, data),
  getAllOrders: (params) => api.get('/orders/all', { params }),
  getOrderStats: () => api.get('/orders/stats')
};

// Admin APIs
export const adminAPI = {
  addProduct: (data) => api.post('/admin/products', data),
  updateProduct: (id, data) => api.put(`/admin/products/${id}`, data),
  deleteProduct: (id) => api.delete(`/admin/products/${id}`),
  bulkDeleteProducts: (productIds) => api.post('/admin/products/bulk-delete', { productIds }),
  getAnalytics: () => api.get('/admin/analytics'),
  exportAnalytics: (type) => api.get('/admin/analytics/export', { 
    params: { type },
    responseType: 'blob'
  }),
  moderateReview: (reviewId, data) => api.put(`/admin/reviews/${reviewId}/moderate`, data),
  getAllUsers: (params) => api.get('/admin/users', { params }),
  updateUserRole: (userId, data) => api.put(`/admin/users/${userId}/role`, data)
};

export default api;
