import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { productAPI } from '../services/api';
import ProductCard from '../components/ProductCard';

const Home = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchFeaturedProducts();
  }, []);

  const fetchFeaturedProducts = async () => {
    try {
      const response = await productAPI.getProducts({ limit: 8, sort: 'rating' });
      setProducts(response.data.products);
    } catch (error) {
      console.error('Error fetching products:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <section style={{ padding: '60px 0', textAlign: 'center' }}>
        <h1 style={{ fontSize: '48px', marginBottom: '20px' }}>
          Welcome to ShopHub
        </h1>
        <p style={{ fontSize: '20px', color: '#666', marginBottom: '30px' }}>
          Discover amazing products at great prices
        </p>
        <Link to="/products">
          <button className="btn btn-primary" style={{ fontSize: '18px', padding: '15px 40px' }}>
            Shop Now
          </button>
        </Link>
      </section>

      <section style={{ paddingBottom: '60px' }}>
        <h2 style={{ fontSize: '32px', marginBottom: '30px', textAlign: 'center' }}>
          Featured Products
        </h2>

        {loading ? (
          <div className="loading">Loading products...</div>
        ) : (
          <div className="product-grid">
            {products.map((product) => (
              <ProductCard key={product._id} product={product} />
            ))}
          </div>
        )}

        <div style={{ textAlign: 'center', marginTop: '40px' }}>
          <Link to="/products">
            <button className="btn btn-secondary">View All Products</button>
          </Link>
        </div>
      </section>
    </div>
  );
};

export default Home;
