import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { adminAPI, productAPI } from '../services/api';

const ProductForm = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    ProductName: '',
    ProductDescription: '',
    price: '',
    category: '',
    brand: '',
    image_link: '',
    stock: ''
  });

  useEffect(() => {
    if (id) fetchProduct();
  }, [id]);

  const fetchProduct = async () => {
    try {
      const response = await productAPI.getProductById(id);
      const product = response.data;
      setFormData({
        ProductName: product.ProductName || '',
        ProductDescription: product.ProductDescription || '',
        price: product.price || '',
        category: product.category || '',
        brand: product.brand || '',
        image_link: product.image_link || '',
        stock: product.stock || ''
      });
    } catch (error) {
      alert('Failed to load product');
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      if (id) {
        await adminAPI.updateProduct(id, formData);
        alert('Product updated successfully');
      } else {
        await adminAPI.addProduct(formData);
        alert('Product added successfully');
      }
      navigate('/admin');
    } catch (error) {
      alert(id ? 'Failed to update product' : 'Failed to add product');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ background: '#fff', padding: '30px', borderRadius: '8px', boxShadow: '0 2px 8px rgba(0,0,0,0.1)' }}>
      <h2 style={{ marginBottom: '25px' }}>{id ? 'Edit Product' : 'Add New Product'}</h2>

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Product Name *</label>
          <input
            type="text"
            name="ProductName"
            className="form-control"
            value={formData.ProductName}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Description *</label>
          <textarea
            name="ProductDescription"
            className="form-control"
            rows="4"
            value={formData.ProductDescription}
            onChange={handleChange}
            required
          />
        </div>

        <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '20px' }}>
          <div className="form-group">
            <label>Price *</label>
            <input
              type="number"
              name="price"
              className="form-control"
              step="0.01"
              value={formData.price}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Stock</label>
            <input
              type="number"
              name="stock"
              className="form-control"
              value={formData.stock}
              onChange={handleChange}
            />
          </div>
        </div>

        <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '20px' }}>
          <div className="form-group">
            <label>Category</label>
            <input
              type="text"
              name="category"
              className="form-control"
              value={formData.category}
              onChange={handleChange}
            />
          </div>

          <div className="form-group">
            <label>Brand</label>
            <input
              type="text"
              name="brand"
              className="form-control"
              value={formData.brand}
              onChange={handleChange}
            />
          </div>
        </div>

        <div className="form-group">
          <label>Image URL *</label>
          <input
            type="url"
            name="image_link"
            className="form-control"
            placeholder="Enter valid image URL"
            value={formData.image_link}
            onChange={handleChange}
            required
          />
        </div>

        {formData.image_link && (
          <div style={{ marginBottom: '20px', textAlign: 'center' }}>
            <img
              src={formData.image_link}
              alt="Preview"
              style={{
                width: '200px',
                height: '200px',
                objectFit: 'cover',
                borderRadius: '8px',
                border: '1px solid #ddd'
              }}
              onError={(e) => e.target.src = 'https://via.placeholder.com/200?text=Invalid+Image'}
            />
          </div>
        )}

        <div style={{ display: 'flex', gap: '10px' }}>
          <button type="submit" className="btn btn-primary" disabled={loading}>
            {loading ? 'Saving...' : id ? 'Update Product' : 'Add Product'}
          </button>
          <button
            type="button"
            className="btn btn-secondary"
            onClick={() => navigate('/admin')}
          >
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default ProductForm;
