import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { adminAPI, productAPI } from '../services/api';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';

const AdminDashboard = () => {
  const [analytics, setAnalytics] = useState(null);
  const [products, setProducts] = useState([]);
  const [selectedProducts, setSelectedProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [analyticsRes, productsRes] = await Promise.all([
        adminAPI.getAnalytics(),
        productAPI.getProducts({ limit: 50 })
      ]);
      setAnalytics(analyticsRes.data);
      setProducts(productsRes.data.products);
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleExport = async (type) => {
    try {
      const response = await adminAPI.exportAnalytics(type);
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `${type}-export.csv`);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (error) {
      alert('Export failed');
    }
  };

  const handleDeleteProduct = async (id) => {
    if (window.confirm('Are you sure you want to delete this product?')) {
      try {
        await adminAPI.deleteProduct(id);
        fetchData();
      } catch (error) {
        alert('Failed to delete product');
      }
    }
  };

  const handleBulkDelete = async () => {
    if (selectedProducts.length === 0) {
      alert('Please select products to delete');
      return;
    }

    if (window.confirm(`Delete ${selectedProducts.length} products?`)) {
      try {
        await adminAPI.bulkDeleteProducts(selectedProducts);
        setSelectedProducts([]);
        fetchData();
      } catch (error) {
        alert('Bulk delete failed');
      }
    }
  };

  const toggleProductSelection = (id) => {
    setSelectedProducts(prev =>
      prev.includes(id) ? prev.filter(p => p !== id) : [...prev, id]
    );
  };

  if (loading) {
    return <div className="loading">Loading dashboard...</div>;
  }

  return (
    <div>
      {/* === SUMMARY CARDS === */}
      <div style={{ 
        display: 'grid', 
        gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', 
        gap: '20px', 
        marginBottom: '30px' 
      }}>
        {[
          { label: 'Total Products', value: analytics?.summary.totalProducts },
          { label: 'Total Users', value: analytics?.summary.totalUsers },
          { label: 'Total Orders', value: analytics?.summary.totalOrders },
          { label: 'Total Revenue', value: `$${analytics?.summary.totalRevenue?.toFixed(2)}` },
        ].map((card, i) => (
          <div key={i} style={{ background: '#fff', padding: '20px', borderRadius: '8px', boxShadow: '0 2px 8px rgba(0,0,0,0.1)' }}>
            <div style={{ fontSize: '14px', color: '#666', marginBottom: '8px' }}>{card.label}</div>
            <div style={{ fontSize: '28px', fontWeight: 'bold' }}>{card.value}</div>
          </div>
        ))}
      </div>

      {/* === SALES CHART === */}
      {analytics?.salesByDate?.length > 0 && (
        <div style={{ background: '#fff', padding: '20px', borderRadius: '8px', marginBottom: '30px', boxShadow: '0 2px 8px rgba(0,0,0,0.1)' }}>
          <h2 style={{ marginBottom: '20px' }}>Sales Overview (Last 30 Days)</h2>
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={analytics.salesByDate}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="_id" />
              <YAxis />
              <Tooltip />
              <Bar dataKey="totalSales" fill="#4a90e2" />
            </BarChart>
          </ResponsiveContainer>
        </div>
      )}

      {/* === EXPORT SECTION === */}
      <div style={{ background: '#fff', padding: '20px', borderRadius: '8px', marginBottom: '30px', boxShadow: '0 2px 8px rgba(0,0,0,0.1)' }}>
        <h2>Export Data</h2>
        <div style={{ display: 'flex', gap: '10px', marginTop: '15px' }}>
          <button onClick={() => handleExport('products')} className="btn btn-secondary">Export Products</button>
          <button onClick={() => handleExport('orders')} className="btn btn-secondary">Export Orders</button>
          <button onClick={() => handleExport('users')} className="btn btn-secondary">Export Users</button>
        </div>
      </div>

      {/* === PRODUCTS TABLE === */}
      <div style={{ background: '#fff', padding: '20px', borderRadius: '8px', boxShadow: '0 2px 8px rgba(0,0,0,0.1)' }}>
        <div className="flex-between mb-20">
          <h2>Products Management</h2>
          {selectedProducts.length > 0 && (
            <button onClick={handleBulkDelete} className="btn btn-danger">
              Delete Selected ({selectedProducts.length})
            </button>
          )}
        </div>

        <div style={{ overflowX: 'auto' }}>
          <table style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead>
              <tr style={{ borderBottom: '2px solid #eee', textAlign: 'left' }}>
                <th style={{ padding: '10px' }}>
                  <input
                    type="checkbox"
                    onChange={(e) => {
                      if (e.target.checked) {
                        setSelectedProducts(products.map(p => p._id));
                      } else {
                        setSelectedProducts([]);
                      }
                    }}
                    checked={selectedProducts.length === products.length && products.length > 0}
                  />
                </th>
                <th style={{ padding: '10px' }}>Image</th>
                <th style={{ padding: '10px' }}>Product Name</th>
                <th style={{ padding: '10px' }}>Price</th>
                <th style={{ padding: '10px' }}>Rating</th>
                <th style={{ padding: '10px' }}>Reviews</th>
                <th style={{ padding: '10px' }}>Actions</th>
              </tr>
            </thead>
            <tbody>
              {products.map((product) => (
                <tr key={product._id} style={{ borderBottom: '1px solid #eee' }}>
                  <td style={{ padding: '10px' }}>
                    <input
                      type="checkbox"
                      checked={selectedProducts.includes(product._id)}
                      onChange={() => toggleProductSelection(product._id)}
                    />
                  </td>
                  <td style={{ padding: '10px' }}>
                    <div style={{
                      width: '70px',
                      height: '70px',
                      borderRadius: '8px',
                      overflow: 'hidden',
                      background: '#f9f9f9',
                      display: 'flex',
                      justifyContent: 'center',
                      alignItems: 'center',
                    }}>
                      <img
                        src={product.image_link}
                        alt={product.ProductName}
                        style={{
                          width: '100%',
                          height: '100%',
                          objectFit: 'cover'
                        }}
                        onError={(e) => e.target.src = 'https://via.placeholder.com/70?text=No+Image'}
                      />
                    </div>
                  </td>
                  <td style={{ padding: '10px', maxWidth: '250px', whiteSpace: 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis' }}>
                    {product.ProductName}
                  </td>
                  <td style={{ padding: '10px' }}>${product.price?.toFixed(2)}</td>
                  <td style={{ padding: '10px' }}>‚≠ê {product.averageRating?.toFixed(1) || 'N/A'}</td>
                  <td style={{ padding: '10px' }}>{product.totalReviews || 0}</td>
                  <td style={{ padding: '10px' }}>
                    <div style={{ display: 'flex', gap: '6px' }}>
                      <button
                        onClick={() => navigate(`/admin/edit-product/${product._id}`)}
                        className="btn btn-secondary"
                        style={{ padding: '5px 10px', fontSize: '13px' }}
                      >
                        Edit
                      </button>
                      <button
                        onClick={() => handleDeleteProduct(product._id)}
                        className="btn btn-danger"
                        style={{ padding: '5px 10px', fontSize: '13px' }}
                      >
                        Delete
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
